import React, { Component } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Amplify, {API,graphqlOperation} from 'aws-amplify';
import { withAuthenticator} from 'aws-amplify-react';
import { createTodo, deleteTodo } from "./graphql/mutations"
import { listTodos } from "./graphql/queries"
import { onCreateTodo } from "./graphql/subscriptions"
import { Auth } from 'aws-amplify'
import aws_exports from './aws-exports'; // specify the location of aws-exports.js file on your project
Amplify.configure(aws_exports);


import { Authenticator, SignIn } from 'aws-amplify-react';

// The override prop tells the Authenticator that the SignUp component is not hidden but overridden
<Authenticator hideDefault={true}>
  <SignIn />
  <MyCustomSignUp override={'SignUp'}/> 
</Authenticator>

class MyCustomSignUp extends Component {
  constructor() {
    super();
    this.gotoSignIn = this.gotoSignIn.bind(this);
  }

  gotoSignIn() {
    // to switch the authState to 'signIn'
    this.props.onStateChange('signIn',{});
  }
}

class App extends Component {
  //create initial state
  state = {name: '', date: '', description: '', hours: '', todos: [] }

  //update state when user types into inputs
  onChange = event => {
    this.setState({ [event.target.name]: event.target.value })
  }

  //update todos in gui
  async componentDidMount() {
    try {
      const todos = await API.graphql(graphqlOperation(listTodos))
      console.log('showing todos: ', todos)
      this.setState({ todos: todos.data.listTodos.items })
    } catch (err) {
      console.log('error fetching data: ', err)
    }
    API.graphql(graphqlOperation(onCreateTodo)).subscribe({
      next: eventData => {
        const data = eventData.value.data.onCreateTodo
        console.log('data: ', data)
        const todos = [
          ...this.state.todos, 
          data
        ]
        this.setState({ todos })
      }
    })
  }

  //define function to execute mutation
  CreateTodo = async() => {
    if (
      this.state.name === '' ||
      this.state.date === '' || 
      this.state.description === '' || 
      this.state.hours === ''
    ) return
    try {
      const todo = { 
        name: this.state.name, 
        date: this.state.date, 
        description: this.state.description,
        hours: this.state.hours 
      }
      const todos = [...this.state.todos, todo]
      this.setState({ name: '', date: '', description: '' , hours: '', todos })
      await API.graphql(graphqlOperation(createTodo, todo))
      console.log('todo successfully created!', todo)
      this.listTodos();
    } catch (err) {
      console.log('error creating todo ...', err)
    }
  }

  DeleteTodo = async(id) => {
    try{
      // "Variable 'input' has an invalid value. Expected type 'Map' but was 'String'. Variables for input objects must be an instance of type 'Map'."
      const array = [['id', id]]
      const idMap = new Map(array);
      const noteId = {
        input: idMap
      }; 
      console.log('noteId looks like this', { noteId })
      await API.graphql(graphqlOperation(deleteTodo, noteId));
      console.log('todo successfully deleted!', { noteId })
      this.listNotes();
    } catch (err) {
      console.log('error deleting todo ...', err)
    }

  }

  //render the component main
  render() {
    const data = [].concat(this.state.todos).map((item,i)=> 
      <div className="alert alert-primary alert-dismissible show" role="alert">
        <span key={item.i}>
          Familienname: {item.name} Datum: {item.date} Elternarbeit: {item.description} Stunden: {item.hours} 
        </span>
        <button 
          key={item.i} 
          type="button" 
          className="close" 
          data-dismiss="alert" 
          aria-label="Close" 
          onClick={this.DeleteTodo.bind(this, item.id)}>
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      )
    return (
      <div className="App">
        <div className="container">
          <legend>Trag' deine Elternstunden in das Formular ein.</legend>
            <p>
              <input id="name" type="text" placeholder="Familienname" className="form-control" value={this.state.name} onChange={this.onChange} name='name' />
            </p>
            <p>
              <input id="date" type="text" placeholder="Datum der Elternarbeit" className="form-control" value={this.state.date} onChange={this.onChange} name='date' />
            </p>
            <p>
            <input id="description" type="text" placeholder="Beschreibung der Elternarbeit" className="form-control" value={this.state.description} onChange={this.onChange} name='description' />
            </p>
            <p>
              <input id="hours" type="text" placeholder="Anzahl Elternstunden" className="form-control" value={this.state.hours} onChange={this.onChange} name='hours' />
            </p>
            <button type="submit" className="btn btn-primary" onClick={this.CreateTodo}>Eintrag vornehmen</button>
        </div>
        <hr></hr>
        <div className="container">
          <legend>Erfasste Elternstunden</legend>
          <p>Falsch erfasste Stunden bitten löschen und neu eintragen.</p>
            {data}
        </div>  
      </div>

    )
  }
}

// export default withAuthenticator(App, {
//   signUpConfig: {
//     hiddenDefaults: ['phone_number']
//   },
//   includeGreetings: true,
//   usernameAttributes: 'email' 
// });

export default App
