import React, { Component } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Amplify, {API,graphqlOperation} from 'aws-amplify';
import { withAuthenticator} from 'aws-amplify-react'; 
import aws_exports from './aws-exports'; // specify the location of aws-exports.js file on your project
Amplify.configure(aws_exports);

// define the todo
const CreateTodo = `
  mutation($name: String!, $description: String!, , $hours: String!) {
    createTodo(input: {
      name: $name
      description: $description
      hours: $hours
    }) {
      id name description hours
    }
  }
`
// list todo
const ListTodos = `
  query {
    listTodos {
      items {
        id name description hours
      }
    }
  }
`

// define the subscription for todos
// const OnCreateTodo = `
//   subscription {
//     onCreateTodo {
//       id name description hours
//     }
//   }
// `

class App extends Component {
  //create initial state
  state = {name: '', description: '' , hours: '', todos: [] }

  //update state when user types into inputs
  onChange = event => {
    this.setState({ [event.target.name]: event.target.value })
  }

  //update todos in gui
  async componentDidMount() {
    try {
      const todos = await API.graphql(graphqlOperation(ListTodos))
      console.log('showing todos: ', todos)
      this.setState({ todos: todos.data.listTodos.items })
    } catch (err) {
      console.log('error fetching data: ', err)
    }
    // API.graphql(graphqlOperation(OnCreateTodo))
    // .subscribe({
    //   next: eventData => {
    //     const data = eventData.value.data.onCreateTodo
    //     console.log('data: ', data)
    //     const todos = [
    //       ...this.state.todos.filter(
    //         response => response.name !== data.name && 
    //         response.description !== data.description && 
    //         response.hours !== data.hours
    //       ),
    //       data
    //     ]
    //     this.setState({ todos })
    //   }
    // })
  }

  //define function to execute mutation
  CreateTodo = async() => {
    if (
      this.state.name === '' || 
      this.state.description === '' || 
      this.state.hours === ''
    ) return
    try {
      const todo = { 
        name: this.state.name, 
        description: this.state.description,
        hours: this.state.hours 
      }
      await API.graphql(graphqlOperation(CreateTodo, todo))
      this.setState({ name: '', description: '' , hours: '' })
      console.log('todo successfully created!', todo)
      this.listNotes();
    } catch (err) {
      console.log('error creating todo ...', err)
    }
  }

  //render the component
  render() {
    return (
      <div className="App">
        <div className="container">
          <legend>Trag' deine Elternstunden in das Formular ein.</legend>
            <p>
              <input id="name" type="text" placeholder="Familienname" className="form-control" value={this.state.name} onChange={this.onChange} name='name' />
            </p>
            <p>
            <input id="description" type="text" placeholder="Beschreibung der Elternarbeit" className="form-control" value={this.state.description} onChange={this.onChange} name='description' />
            </p>
            <p>
              <input id="hours" type="text" placeholder="Anzahl Elternstunden" className="form-control" value={this.state.hours} onChange={this.onChange} name='hours' />
            </p>
            <button type="submit" className="btn btn-primary" onClick={this.CreateTodo}>Eintrag vornehmen</button>
        </div>
        <div className="container">
            {
              this.state.todos.map((response, i) => (
                <div key={i}>
                  <p>{response.name}</p>
                  <p>{response.description}</p>
                  <p>{response.hours}</p>
                </div>
              ))
            }
        </div>  
      </div>

    )
  }
}
export default withAuthenticator(App, { includeGreetings: true });
